<template>
	<view class="user-info-area">
		<image 
			v-if="user.openid"
			webp
			class="user-info-area-avatar" 
			@error='imageErrorHandler'
			:src="`${avatarUrl}?random=${new Date().getTime()}`"
			mode="scaleToFill">
		</image>
		<image
			v-else
			class="user-info-area-avatar" 
			@error='imageErrorHandler'
			:src='base64Url'
			mode="scaleToFill">
		</image>
		<text class="user-info-area-username">{{user.nickname}}</text>
		<button class="login-button" v-if="!user.openid" @click="login()">微信登录</button>
		<button class="login-button" v-else @click="logout()">退出登录</button>
	</view>
	<uni-list>
		<uni-list-item 
			v-for="(item, index) in list" 
			:title="item.title" 
			:to="item.to"
			:thumb="item.iconSrc" 
			thumbSize="sm" 
			showArrow 
			:key="index">
		</uni-list-item>
	</uni-list>
</template>

<script>
	import { mapState, mapMutations } from 'vuex'
	export default {
		onShow() {
			console.log('show');
			const openid = this.user.openid
			// if(openid) {
			// 	uni.request({
			// 		url: 'https://api.moeyouko.top/user/basic',
			// 		method: "GET",
			// 		data: {
			// 			openid
			// 		},
			// 		success: (res) => {
			// 			console.log(res);
			// 			this.avatarUrl = res.data.avatarUrl
			// 			this.setUser({
			// 				user: {...res.data}
			// 			})
			// 		}
			// 	})
			// }
			this.user.openid && (this.avatarUrl = this.user.avatarUrl)
		},
		data() {
			return {
				list: [
					{
						title: '个人信息',
						iconSrc: '/static/images/user_info.png',
						to: '/subpackages/me/user-info/user-info'
					},
					{
						title: '关于\"工程创新\"',
						iconSrc: '/static/images/about.png',
						to: '/subpackages/me/about/about'
					},
				],
				avatarUrl: 'https://api.moeyouko.top/static/image/logo.jpg',
				base64Url: 'data:image/jpeg;base64,'
			}
		},
		computed: {
			...mapState([
				'user'
			])
		},
		methods: {
			login() {
				uni.showLoading({
					title: '正在登录...',
					mask: true
				});
				const _this = this
				uni.login({
				  provider: 'weixin',
				  scopes: 'auth_user',
				  success: function (loginRes) {
					uni.request({
						url: `https://api.moeyouko.top/user/?code=${loginRes.code}`,
						success: (res) => {
							console.log('logo', res);
							if(res.statusCode === 200 || res.statusCode === 304){
								_this.setUser({
									user: res.data
								})
								uni.setStorage({
									key: 'user',
									data: res.data,
									success: function() {
										uni.setStorage({
											key: 'expire',
											data: new Date().getTime() + 7 * 24 * 60 * 60 * 1000
										})
									},
									complete: function(){
										uni.hideLoading()
									}
								})
							}else {
								uni.showToast({
									title: '登陆失败',
									icon: 'error'
								})
							}
						}
					})
				  }
				});
			},
			logout() {
				uni.showLoading({
					title: '正在退出...',
					mask: true
				})
				this.removeUser()
				try {
					uni.clearStorageSync();
				} catch (e) {
					// error
				}
				this.avatarUrl = 'https://api.moeyouko.top/static/image/logo.jpg'
				uni.hideLoading()
			},
			imageErrorHandler() {
				this.avatarUrl = this.base64Url
			},
			...mapMutations(['setUser', 'removeUser'])
		},
		watch: {
			avatarUrl(newUrl, oldUrl) {
				console.log(newUrl?.slice(0,50), oldUrl);
			}
		}
	}
</script>

<style lang="scss">
	.user-info-area {
		height: 350rpx;
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;
		background-color: #1165BA;
		
		&-avatar {
			height: 120rpx;
			width: 120rpx;
			border-radius: 50%;
			margin-bottom: 20rpx;
		}
		
		&-username {
			color: #fff;
		}
		
		.login-button {
			margin-top: 10px;
			color: #1165BA;
		}
	}
</style>
